import React from 'react';
import { toast } from 'react-toastify';

import { Success, Danger, Warning, Info } from './components';
import { ToastProps } from './types';

export const iToast = {
  success(config: ToastProps) {
    toast.success(<Success {...config} />);
  },
  error(config: ToastProps) {
    toast.error(<Danger {...config} />);
  },
  warning(config: ToastProps) {
    toast.warning(<Warning {...config} />);
  },
  info(config: ToastProps) {
    toast.info(<Info {...config} />);
  },
};
